//populating jtable from driver database
    public ArrayList<drivers> driverlist() {
        ArrayList<drivers> driverlist = new ArrayList<>();
        try {

            Class.forName("com.mysql.jdbc.Driver");
            connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/fleetdb", "root", "");
            String sql = "Select * from driver";
            ps = connection.prepareStatement(sql);
            rs = ps.executeQuery();
            drivers driver;
            while (rs.next()) {
                driver = new drivers(rs.getInt("driver_id"),
                        rs.getString("First_name"),
                        rs.getString("Last_name"),
                        rs.getString("Gender"),
                        rs.getInt("Phone_number"));
                driverlist.add(driver);

            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
        }
        return driverlist;

    }

    public void show_driver() {
        ArrayList<drivers> list = driverlist();
        DefaultTableModel model = (DefaultTableModel) jTable_Display_Driver.getModel();
        Object[] row = new Object[6];
        for (int i = 0; i < list.size(); i++) {
            row[0] = list.get(i).getDriver_id();
            row[1] = list.get(i).getFirst_name();
            row[2] = list.get(i).getLast_name();
            row[3] = list.get(i).getGender();
            row[4] = list.get(i).getPhone_number();
            model.addRow(row);
        }
    }

